// This file contains utilities for playing sounds in the application.
// Sounds are encoded in Base64 to avoid needing separate audio files.

// A short, pleasant chime for successful actions like request approval.
// Source: Public domain sound, converted to Base64.
const APPROVAL_SOUND = "data:audio/mpeg;base64,";

// A short, ticking sound for warnings.
// Source: Public domain sound, converted to Base64.
const WARNING_SOUND = "data:audio/mpeg;base64,";

let audioContext: AudioContext;

const getAudioContext = () => {
    if (typeof window !== 'undefined' && !audioContext) {
        audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
    }
    return audioContext;
}

export const playSound = (soundData: string) => {
    try {
        const context = getAudioContext();
        if (!context) return;
        
        // Check if the audio context is running, resume if it's not (due to browser auto-play policies)
        if (context.state === 'suspended') {
            context.resume();
        }
        
        const audio = new Audio(soundData);
        
        // Play the sound, and catch any errors (e.g., user hasn't interacted with the page yet)
        audio.play().catch(error => {
            console.warn('Could not play sound, likely due to browser auto-play policy.', error);
        });
    } catch (e) {
        console.error("Failed to play sound", e);
    }
};

export { APPROVAL_SOUND, WARNING_SOUND };
